import React, { useContext } from "react";
import "./WeeklySchedule.css";
import { Row, Col } from "reactstrap";
import { useData } from "./DataContext";

const WeeklySchedule1 = () => {

  const { busyData, classroomData, coursesData, servicesData } = useData();
  console.log("data check: ", servicesData);
  const timeslots = ["8:30", "9:30", "10:30", "11:30", "12:30", "13:30", "14:30", "15:30"]
  const scheduleData = {
    "1": {
    Monday: ["", "31", "", "", "", "", "", ""],
    Tuesday: ["", "", "", "", "", "", "", ""],
    Wednesday: ["", "", "", "", "", "", "", ""],
    Thursday: ["", "", "", "", "", "", "", ""],
    Friday: ["", "", "", "", "", "", "", ""]
  },
    "2": {
      Monday: ["", "32", "", "", "", "", "", ""],
      Tuesday: ["", "", "", "", "", "", "", ""],
      Wednesday: ["", "", "", "", "", "", "", ""],
      Thursday: ["", "", "", "", "", "", "", ""],
      Friday: ["", "", "", "", "", "", "", ""]
    },
    "3": {
      Monday: ["", "33", "", "", "", "", "", ""],
      Tuesday: ["", "", "", "", "", "", "", ""],
      Wednesday: ["", "", "", "", "", "", "", ""],
      Thursday: ["", "", "", "", "", "", "", ""],
      Friday: ["", "", "", "", "", "", "", ""]
    },
    "4": {
      Monday: ["", "34", "", "", "", "", "", ""],
      Tuesday: ["", "", "", "", "", "", "", ""],
      Wednesday: ["", "", "", "", "", "", "", ""],
      Thursday: ["", "", "", "", "", "", "", ""],
      Friday: ["", "", "", "", "", "", "", ""]
    }
  };
 
  if(servicesData){
    let classNumber, day, time;
    for (let i = 0; i < servicesData.length; i++) {
      for (let j = 0; j <  servicesData[i][0].length; j++) {
        if(servicesData[i][0][j] == 1)
          console.log("gæ var")
        
      }
     
      switch (servicesData[i][1]) {
        case 'Tuesday':
          scheduleData[0][2] = "52";
          break;
      
        default:
          break;
      }
    }  
  }


    //1123 1=1.sınıf, 1=pazartesi günü, 2=öğleden sonra, 3=günün 2.dersi(1=1.ders, 2=1.dersin kodu, 3= günün 2.dersi)
    return (
      <div>
      <Row>
        {Object.keys(scheduleData).map(classNumber => (
          <Col key={classNumber}>
            <h1>{classNumber}. Class</h1>
            <table className="content-table">
              <thead>
                <tr>
                  <th>Day</th>
                  {timeslots.map((time, index) => (
                      <th key={index}>{time}</th>
                    ))}
                </tr>
              </thead>
              <tbody>
                {Object.keys(scheduleData[classNumber]).map(day => (
                  <tr key={day}>
                    <td>{day}</td>
                    {scheduleData[classNumber][day].map((className, index) => (
                      <td key={index}>{className}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </Col>
        ))}
      </Row>
    </div>
  );
};
export default WeeklySchedule1;
