import React, { useContext } from "react";
import "./WeeklySchedule.css";
import { Row, Col } from "reactstrap";
import { useData } from "./DataContext";

const WeeklySchedule1 = () => {
  const classNumber = "1234"
  const { busyData, classroomData, coursesData, servicesData } = useData();
  console.log("data check: ", servicesData);
  const scheduleData = {
    "1": {
    Monday: ["", "31", "", "", "", "", "", ""],
    Tuesday: ["", "", "", "", "", "", "", ""],
    Wednesday: ["", "", "", "", "", "", "", ""],
    Thursday: ["", "", "", "", "", "", "", ""],
    Friday: ["", "", "", "", "", "", "", ""]
  },
    "2": {
      Monday: ["", "31", "", "", "", "", "", ""],
      Tuesday: ["", "", "", "", "", "", "", ""],
      Wednesday: ["", "", "", "", "", "", "", ""],
      Thursday: ["", "", "", "", "", "", "", ""],
      Friday: ["", "", "", "", "", "", "", ""]
    },
    "3": {
      Monday: ["", "31", "", "", "", "", "", ""],
      Tuesday: ["", "", "", "", "", "", "", ""],
      Wednesday: ["", "", "", "", "", "", "", ""],
      Thursday: ["", "", "", "", "", "", "", ""],
      Friday: ["", "", "", "", "", "", "", ""]
    },
    "4": {
      Monday: ["", "31", "", "", "", "", "", ""],
      Tuesday: ["", "", "", "", "", "", "", ""],
      Wednesday: ["", "", "", "", "", "", "", ""],
      Thursday: ["", "", "", "", "", "", "", ""],
      Friday: ["", "", "", "", "", "", "", ""]
    }
  };
 
  if(servicesData){
    for (let index = 0; index < servicesData.length; index++) {
      switch (servicesData[index][1]) {
        case 'Tuesday':
          scheduleData[0][2] = "52";
          break;
      
        default:
          break;
      }
    }  
  }


    //1123 1=1.sınıf, 1=pazartesi günü, 2=öğleden sonra, 3=günün 2.dersi(1=1.ders, 2=1.dersin kodu, 3= günün 2.dersi)
    return (
          <div>
      <Row>
        {Object.keys(scheduleData).map(classNumber => (
            <table className="content-table">
              <thead>
                <tr>
                  <th>Day</th>
                  <th>Time</th>
                  <th>1. Class</th>
                  <th>2. Class</th>
                  <th>3. Class</th>
                  <th>4. Class</th>
                  <th>5. Class</th>
                  <th>6. Class</th>
                  <th>7. Class</th>
                  <th>8. Class</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>{day}</td>
                  {scheduleData[day].map((className, index) => (
                    <td key={index}>{className}</td>
                  ))}
                </tr>
              </tbody>
            </table>
        ))}
      </Row>
    </div>
  );
};
export default WeeklySchedule1;
