import React, { useState } from "react";
import { useData } from "./DataContext";
import { Row, Col } from "reactstrap";

function DeletePanel() {
  const { coursesData, setCoursesData } = useData();

  const handleDeleteCourse = () => {
    var key = document.getElementById("courseToDelete").key;
    var value = document.getElementById("courseToDelete").value;

    if (key && value) {
      setCoursesData((prevCoursesData) => {
        const updatedCourses = [...prevCoursesData];
        updatedCourses.splice(key, 1);
        return updatedCourses;
      });
    } else {
      alert("Select one of the courses to delete");
    }
  };
  return (
    <div>
      <h1>Delete Course</h1>
      <Row>
        <Col>
          <label htmlFor="courseToDelete">Select a course to delete:</label>
          <select id="courseToDelete">
            <option value="">Select a course</option>
            {coursesData.map((course, index) => (
              <option key={index} value={index}>
                {course[0]} - {course[1]}
              </option>
            ))}
          </select>
        </Col>
      </Row>
      <div>
        <input
          type="button"
          id="deleteCourse"
          value="Delete Course"
          onClick={handleDeleteCourse}
        />
      </div>
    </div>
  );
}

export default DeletePanel;
