import React from "react";
import WeeklySchedule1 from "./WeeklySchedule1";

function MakeSchedule({ busyData, classroomData, coursesData, servicesData }) {

  const handleSetDataToWeeklySchedule = (data) => {
    // Verinin 1101 ID'li hücreye atanması
    const cellData = data.map((item, index) => (
      <td key={index} id="1101">{item}</td>
    ));
    return cellData;
  };

  // servicesData'dan veriyi alıp işleme
  const weeklyScheduleData = servicesData.map(item => item.course);

  return (
    <div>
      <h2>Busy Data:</h2>
      {busyData ? (
        <ul>
          {busyData.map((item, index) => (
            <li key={index}>{JSON.stringify(item)}</li>
          ))}
        </ul>
      ) : (
        <p>No busy data available</p>
      )}

      <h2>Classroom Data:</h2>
      {classroomData ? (
        <ul>
          {classroomData.map((item, index) => (
            <li key={index}>{JSON.stringify(item)}</li>
          ))}
        </ul>
      ) : (
        <p>No classroom data available</p>
      )}

      <h2>Courses Data:</h2>
      {coursesData ? (
        <ul>
          {coursesData.map((item, index) => (
            <li key={index}>{JSON.stringify(item)}</li>
          ))}
        </ul>
      ) : (
        <p>No courses data available</p>
      )}

      <h2>Services Data:</h2>
      {servicesData ? (
        <ul>
          {servicesData.map((item, index) => (
            <li key={index}>{JSON.stringify(item)}</li>
          ))
          }
        </ul>
      ) : (
        <p>No services data available</p>
      )}
       <WeeklySchedule1 data={handleSetDataToWeeklySchedule(weeklyScheduleData)} />
    </div>
  );
}

export default MakeSchedule;
