import React, { useState } from "react";
import { useData } from "./DataContext";
import { Row, Col } from "reactstrap";

function DeletePanel() {
  const { coursesData, setCoursesData } = useData();

  const handleDeleteCourse = () => {
    var code = document.getElementById("code").value;
    var name = document.getElementById("name").value;
    var semester = document.getElementById("semester").value;
    var credit = document.getElementById("credit").value;
    var cOrE = document.getElementById("cOrE").value;
    var depOrS = document.getElementById("depOrS").value;
    var student = document.getElementById("student").value;
    var instructor = document.getElementById("instructor").value;
    var hours = document.getElementById("hours").value;

    if (
      code &&
      name &&
      semester &&
      credit &&
      cOrE &&
      depOrS &&
      student &&
      instructor &&
      hours
    ) {
      if (
        !(
          semester == "1" ||
          semester == "2" ||
          semester == "3" ||
          semester == "4"
        )
      ) {
        alert('Semester must be one of these: "1, 2, 3, 4"');
      } else if (isNaN(student) && !isNaN(parseFloat(student))) {
        alert("Number of students must be a number");
      } else {
        setCoursesData((prevCoursesData) => {
          const newCourse = [
            code,
            name,
            semester,
            credit,
            cOrE,
            depOrS,
            student,
            instructor,
            hours,
          ];
          return [...prevCoursesData, newCourse];
        });
      }
    } else {
      alert("Fill in the blanks");
    }
  };
  return (
    <div>
      <h1>Add Course</h1>
      <Row>
        <Col>
          <label htmlFor="courseToDelete">Select a course to delete:</label>
          <select id="courseToDelete">
            <option value="">Select a course</option>
            {coursesData.map((course, index) => (
              <option key={index} value={index}>
                {course[0]} - {course[1]}
              </option>
            ))}
          </select>
        </Col>
      </Row>
      <label htmlFor="hours">Hours preference:</label>
      <select id="hours">
        <option value="3">3</option>
        <option value="2+1">2+1</option>
      </select>
      <div>
        <input
          type="button"
          id="addCourse"
          value="Add Course"
          onClick={handleDeleteCourse}
        />
      </div>
    </div>
  );
}

export default DeletePanel;
